 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    vector generateTrees(int n) {
        if (n == 0)
        {
            vector ans;
            return ans;
        }
        return helper(1 , n);
    }

    vector helper(int left , int right)
    {
        vector ans;
        if (left > right)
        {
            ans.push_back(NULL);
            return ans;
        }

        for (int i = left ; i  leftTrees = helper(left , i-1);
            vector rightTrees = helper(i+1 , right);

            for (auto leftTree : leftTrees)
            {
                for (auto rightTree : rightTrees)
                {
                    TreeNode* root = new TreeNode(i);
                    root->left = leftTree;
                    root->right = rightTree;
                    ans.push_back(root);
                }
            }
        }
        return ans;
    }
};

